import React from 'react'
import './Button.css'

export default props => {
    
    let classes = 'Button '
        classes += props.operation ? 'operation' : ''
        classes += props.double ? 'double' : ''
        clases += props.triple ? 'triple' : ''
    
    return(
        

        <button className={classes}>
            {props.label}
        </button>
    )
}
